"let productline_roadmaps = data.roadmaps.data.productline_roadmaps;
  let productName = data.params.productName;
  let roadmap_id = 9999;

  // Loop through product lines, then products inside each, until you find the product
  for (i = 0; i < productline_roadmaps.length; i++) {
    const roadmaps = productline_roadmaps[i].roadmaps;
    for (j = 0; j < roadmaps.length; j++) {
      comparison_string = "Roadmap for " + productName;
      if (roadmaps[j].name === comparison_string) {
        roadmap_id = roadmaps[j].id;
        break;
      }
    }
  }
  console.log(roadmap_id);
  return roadmap_id;"

"let swimlanes = data.data;
  let csv_rows = [];

  header_row= ["swimlane","card title","description","public","position","status","state","tags","objectives","owners","idea title"];
  csv_rows.push(header_row);

  // For each swimlane
  for (i = 0; i < swimlanes.length; i++) {
    const swimlane_name = swimlanes[i].title;
    const cards = swimlanes[i].cards;

    // For each idea (not card), we add a new row
    for (j = 0; j < cards.length; j++) { 
      card = cards[j];

      const ideas = card.pipeline.ideas;
      const tags = card.tags;
      let tags_string = "";
      for (m = 0; m < tags.length; m++) {
        const tag = tags[m];

        if (m == 0) {
          tags_string = tags_string + tag.tag;
        } else {
          tags_string = tags_string +  ", " + tag.tag;
        }
      }

      const objectives = card.objectives;
      let objectives_string = "";
      for (m = 0; m < objectives.length; m++) {
        const objective = objectives[m];

        if (m == 0) {
          objectives_string = objectives_string + objective.name;
        } else {
          objectives_string = objectives_string +  ", " + objective.name;
        }
      }

      const owners = card.owners;
      let owners_string = "";
      for (m = 0; m < owners.length; m++) {
        const owner = owners[m];
        if (m == 0) {
          owners_string = owners_string + owner.display_name;
        } else {
          owners_string = owners_string +  ", " + owner.display_name;
        }
      }

      const card_description = card.description.replace(",","");

      // Make sure aall cards are added, even if they have no ideas
      if (ideas.length == 0) {
        let csv_row = [];

        // Add card details
        csv_row.push(swimlane_name);
        csv_row.push(card.title);
        csv_row.push(card_description);
        csv_row.push(card.public);
        csv_row.push(card.position);
        csv_row.push(card.status);
        csv_row.push(card.state);
        csv_row.push(tags_string);
        csv_row.push(objectives_string);
        csv_row.push(owners_string);
        csv_row.push("No ideas");

        csv_rows.push(csv_row);
        continue; // Skip to next card
      }

      for (n = 0; n < ideas.length; n++) {
        let csv_row = [];
        idea = ideas[n];

        // Add card details
        csv_row.push(swimlane_name);
        csv_row.push(card.title);
        csv_row.push(card_description);
        csv_row.push(card.public);
        csv_row.push(card.position);
        csv_row.push(card.status);
        csv_row.push(card.state);
        csv_row.push(tags_string);
        csv_row.push(objectives_string);
        csv_row.push(owners_string);

        // Add idea details
        csv_row.push(idea.title);

        // add row per idea
        csv_rows.push(csv_row);
      }
    }
  }

  return csv_rows;"
